import React, { useState, useEffect } from "react";
import { createRoot } from 'react-dom/client';
import "./index.css";

function App(props) {
  const [joke, setJoke] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [jokes, setJokes] = useState([]);
  const [isFetchingJoke, setIsFetchingJoke] = useState(false);

  useEffect(() => {
    searchJokes();
  }, []);

  function searchJokes(limit = 20) {
    setIsFetchingJoke(true);
    fetch(`https://icanhazdadjoke.com/search?term=${searchTerm}&limit=${limit}`, {
      method: "GET",
      headers: {
        Accept: "application/json"
      }
    })
      .then((response) => response.json())
      .then(json => {
        const jokes = json.results;
        setJokes(jokes);
        setIsFetchingJoke(false);
      });    
  }

  function renderJokes() {
    return (
      <ul>
        {jokes.map(item => <li key={item.id}>{item.joke}</li>)}
      </ul> 
    );
  }

  const onTellJoke = () => {
    const randomJoke = jokes[Math.floor(Math.random() * jokes.length)];
    setJoke(randomJoke.joke);
  };

  const onSearchChange = (event) => {
    setSearchTerm(event.target.value);
  }

  function onSearchSubmit(event) {
    event.preventDefault();
    searchJokes();
  }

  return (
    <div>
      <form onSubmit={onSearchSubmit}>
        <input type="text" placeholder="Search for a joke ..." 
         onChange={onSearchChange} />
        <button type="submit">Search</button>
        <button onClick={onTellJoke}>I'm Feeling Funny</button>
      </form>

      {isFetchingJoke
        ? 'searching for jokes...' 
        : jokes.length > 0 
          ? renderJokes()
          : 'No jokes found.'
      }
    </div>
  );
}

const rootElement = document.getElementById('root');
createRoot(rootElement).render(<App />);
