import React, { useState, useEffect } from "react";
import { createRoot } from 'react-dom/client';
import "./index.css";

function App(props) {
  const [joke, setJoke] = useState(null);
  const [searchTerm, setSearchTerm] = useState('');
  const [jokes, setJokes] = useState([]);
  const [isFetchingJoke, setIsFetchingJoke] = useState(false);

  useEffect(() => {
    searchJokes();
  }, []);

  function searchJokes() {
    setIsFetchingJoke(true);
    fetch(`https://icanhazdadjoke.com/search?term=${searchTerm}`, {
      method: "GET",
      headers: {
        Accept: "application/json"
      }
    })
      .then((response) => response.json())
      .then(json => {
        const jokes = json.results;
        setJokes(jokes);
        setIsFetchingJoke(false);
      });    
  }

  const onTellJoke = () => {
    const randomJoke = jokes[Math.floor(Math.random() * jokes.length)];
    setJoke(randomJoke.joke);
  };

  const onSearchChange = (event) => {
    setSearchTerm(event.target.value);
  }

  return (
    <div>
      <form>
        <input type="text" placeholder="Search for a joke ..." 
         onChange={onSearchChange} />
        <button onClick={searchJokes}>Search</button>
        <button onClick={onTellJoke}>Tell me a joke</button>
      </form>

      {isFetchingJoke ? (
        <p>Loading...</p>
      ) : (
        joke ? (
          <p>{joke}</p>
        ) : (
          <p>No jokes found.</p>
        )
      )}
    </div>
  );
}

const rootElement = document.getElementById('root');
createRoot(rootElement).render(<App />);
